import './App.css';
import React from 'react';
import { ApolloClient, ApolloProvider, InMemoryCache, gql } from "@apollo/client";
import Product from './Product';
import { qraphql } from '@apollo/client';
import { HashRouter } from "react-router-dom";
const title = "tech";
const client = new ApolloClient({
  uri: "http://localhost:4000",
  cache: new InMemoryCache()
})
const getProductsQuery = gql`
query($input:CategoryInput){
  category(input:$input){
  name
    products{
      id
    }
  }
  }
    `
//let arr = []
client.query({
  query: getProductsQuery,
  variables: { input: { "title": title } }
}).then(res => res.data.category.products/*.map(el => {
  arr.push(el)})*/
)
class App extends React.Component {
  render() {
    console.log(this.props);
    return (
      //<HashRouter basename={process.env.PUBLIC_URL}>
      <ApolloProvider client={client}>
        <div className="App">
          <ul className="product__list">
            {/*arr.map((el) => <Product key={el.id} id={el.id} />)*/}
          </ul>
        </div>
      </ApolloProvider>
      //</HashRouter>
    )

  };
}
//  "homepage": "https://GlebS1mple.github.io/scandiweb", 
export default qraphql(getProductsQuery)(App);
